// Get the 'hits' array from the first node's output
const hits = items[0].json.hits;

// Function to shuffle the array using Fisher-Yates algorithm
function shuffle(array) {
  for (let i = array.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [array[i], array[j]] = [array[j], array[i]];
  }
  return array;
}

// Shuffle the hits array
const shuffledHits = shuffle(hits);

// Extract the largeImageURL from the first item
const largeImageURL = shuffledHits[0]?.largeImageURL || 'No Image Found';

// Return the result
return [
  {
    json: {
      largeImageURL: largeImageURL
    }
  }
];

