const rawResponse = $json.output || "";

const jsonStart = rawResponse.indexOf('{');
const jsonEnd = rawResponse.lastIndexOf('}');

if (jsonStart === -1 || jsonEnd === -1) {
  throw new Error("Kein valides JSON-Objekt im 'output'-Feld der AI-Antwort gefunden. Antwort war: " + rawResponse);
}

const jsonString = rawResponse.substring(jsonStart, jsonEnd + 1);

let parsed;
try {
  parsed = JSON.parse(jsonString);
} catch (e) {
  throw new Error("Fehler beim Parsen des extrahierten JSON: " + e.message + ". Extrahierter Text war: " + jsonString);
}

// Sonderbehandlung: key_findings ist ein JSON-String, den wir erneut parsen müssen
if (typeof parsed.key_findings === 'string') {
  try {
    const tryParsed = JSON.parse(parsed.key_findings);
    if (Array.isArray(tryParsed)) {
      parsed.key_findings = tryParsed;
    }
  } catch (e) {
    // lassen wie es ist
  }
}

// 🔽 Bereinige störende Bullet-Characters in Textfeldern
const cleanBullets = text =>
  typeof text === 'string'
    ? text.replace(/^[\s]*[•\-\*]+[\s]*/gm, '') // entfernt Bulletpoints am Zeilenanfang
    : text;

parsed.study_strengths = cleanBullets(parsed.study_strengths);
parsed.study_limitations = cleanBullets(parsed.study_limitations);
parsed.ai_summary = cleanBullets(parsed.ai_summary);
parsed.content_idea = cleanBullets(parsed.content_idea);

// Optional: Wenn du willst, dass key_findings immer String bleibt (JSON-Stringified)
if (Array.isArray(parsed.key_findings)) {
  parsed.key_findings = JSON.stringify(parsed.key_findings);
}

return parsed;
